import React, { Component } from 'react';
import './ContactForm.css';
import { Link } from 'react-router-dom';
import axios from 'axios';

class ContactForm extends Component {

    constructor() {
        super()

        this.state = {
            name: '',
            email: '',
            subject: '',
            question: ''
        }

        this.handleChange = this.handleChange.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)
    }

    handleChange = e => {
        this.setState({ [e.target.name]: e.target.value })
    }

    async handleSubmit(e) {
        e.preventDefault()

        const { name, email, subject, question } = this.state

        const form = await axios.post('/api/form', {
            name,
            email,
            subject,
            question
        })
    }

    render() {
    return (
        <div className="form">
            <h3>Quick Contact</h3>
            <h4>Contact us today and one of our team members will be with you shortly!</h4>
            <div>
                <form id="contact" action="" method="post" onSubmit={this.handleSubmit}>
                    <div className="form-contact">
                        <label>Name:</label>
                        <input className="name-input" placeholder="Enter name" type="text" autofocus onChange={this.handleChange} />

                        <label>Email Address:</label>
                        <input className="email-input" placeholder="Enter Email Address" type="email" onChange={this.handleChange} />

                        <label>Subject:</label>
                        <input className="subject-input" placeholder="Enter subject" type="text" onChange={this.handleChange} />

                        <label>Enter your questions here:</label>
                        <textarea className="question-input" placeholder="Type here" onChange={this.handleChange} ></textarea>
                        <input type="submit" value="Submit" />
                    </div>
                </form>
            </div>
        </div>
    )
    }
}

export default ContactForm;
